<template>
  <div class="text-center">
    <section class="flex justify-center p-2 gap-2 relative">
      <div class="bg-base-200 rounded-lg p-2">
        <div
          :class="setDimensions"
          v-if="!generated"
          class="absolute flex items-center justify-center text-sm bg-base-300 opacity-90 rounded-sm z-20"
        >
          <div
            v-if="loading"
            class="items-center my-a justify-center inline-flex"
          >
            <div class="loaderMFA"></div>
          </div>

          <button
            class="uppercase bg-base-300 p-2 z-40"
            v-else
            @click="generateQRCode"
            :class="setDimensions"
          >
            Gerar QR
          </button>
        </div>

        <img
          :class="setDimensions"
          class="rounded-md"
          v-if="base64"
          :src="base64"
          alt="QRCode"
        />
      </div>
    </section>
  </div>
</template>

<script setup>
import { computed } from "vue";

const emits = defineEmits(["generateQR"]);

const props = defineProps({
  base64: {
    type: String,
    default:
      "data:image/png;base64,",
  },
  dimensions: {
    type: Array,
    default: [64, 64],
  },
  loading: {
    type: Boolean,
    default: false,
  },
  generated: {
    type: Boolean,
    default: false,
  },
});

const generateQRCode = () => {
  emits("generateQR");
};

const setDimensions = computed(() => {
  return `w-${props.dimensions[0]} h-${props.dimensions[1]}`;
});
</script>
